import React, { useState } from 'react';

export default function Mfinder() {
    const [name, setName] = useState('');
    const [age, setAge] = useState(null);

    const handleInputChange = (event) => {
        setName(event.target.value);
    };

    const fetchAge = async () => {
        try {
            const response = await fetch(`https://api.agify.io/?name=${name}`);
            const data = await response.json();
            setAge(data.age);
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="MeaningFinder">
            <h1>Meaning Finder</h1>
            <input type="text" placeholder="Enter a name" value={name} onChange={handleInputChange} />
            <button onClick={fetchAge}>Find Meaning</button>
            {age && <p>According to api.agify.io, the name "{name}" has an average age of {age} years old.</p>}
        </div>
    );
}
