    const [age, setAge] = useState(100);

    const getText = (event) => {
        setName(event.target.value);
    }

    const getAge = async () => {

        const resp = await fetch(`https://api.agify.io/?name=${name}`);
        const respjson = await resp.json();
        const respage = respjson.age;
        setAge(respage);
    }
